import '../../App.scss';
import React, { Component } from 'react';
import Video from '../../components/Video/Video.jsx';
import Info from '../../components/Info/Info.jsx';
import Comments from '../../components/Comments/Comments.jsx';
import Sidebar from '../../components/Sidebar/Sidebar.jsx';
import { API_KEY, API_URL } from '../../utils';
import axios from 'axios';



class HomePage extends Component  {

  state = {
      videos: [],
      selectedVideo: {}
  }

    getVideoDetails = (videoId) => {
    axios
        .get(`${API_URL}/${videoId}${API_KEY}`)
        .then((response) => {
            this.setState({
                selectedVideo: response.data
            });
            console.log(response.data)
        });

    }

    componentDidUpdate(prevProps, _prevState) {
        console.log('componentDidUpdate')
        if(prevProps.match.params.videoId === this.props.match.params.videoId){
            console.log('These props are the same as before. No need to get pokemon details');
            return;
        } else {
            console.log('These are new props.');
            this.getVideoDetails(this.props.match.params.videoId);
        }
    }

    componentDidMount() {
        axios
            .get(`${API_URL}${API_KEY}`)
            .then((response) => {
                this.setState({
                    videos: response.data,
                });
                console.log(response.data[0].id)
                const videoId =
                    this.props.match.params.videoId || response.data[0].id;

                this.getVideoDetails(videoId);
            });
    }

changeVideo = (id) => {
     let newVideo = this.state.videos.find(video => video.id === id)

    this.setState({
      selectedVideo: newVideo,
      })
   }

  render() {
    console.log(this.state.selectedVideo)
    return (
      <>
      <Video video={this.state.videos}/>
      <div className="container">
        <div className="container--left">
          <Info video={this.state.selectedVideo}/>
          <Comments videoDetails={this.state.selectedVideo}/>
        </div>
        <div className="container--right">
          <Sidebar
            videos={this.state.videos.filter(video => this.state.selectedVideo.id !== video.id)}
            handleClick={this.changeVideo} />
        </div>
      </div>
      </>
    );
  }
}

export default HomePage;
