const videoURL = id => `${API_URL}videos/${id}/${API_KEY}`

export default class Video extends Component {
    state = {
        videos: [],
    }

  componentDidMount() {
   this.getData()
  }

  getData = () => axios.get(videoURL(this.state.videos)).then(res => {
    this.setState({
      temperature: this.convertTemp(res.data.main.temp).toFixed(2),
      weatherDescription: res.data.weather[0].description
    })
  })

    componentDidMount() {
    axios.get(`${API_URL}videos${API_KEY}`).then((response) => {
      console.log(response);
      this.setState({
        videos: response.data,
      });
    });
    }

    render() {
        return (
            <div className="video">
                <video controls poster={this.state.videos.image}></video>
            </div>
        )
    }
}
