import '../../App.scss';
import React, { Component } from 'react';
import Video from '../../components/Video/Video.jsx';
import Info from '../../components/Info/Info.jsx';
import Comments from '../../components/Comments/Comments.jsx';
import Sidebar from '../../components/Sidebar/Sidebar.jsx';
import { API_KEY, API_URL } from '../../utils';
import axios from 'axios';



class HomePage extends Component  {



  componentDidUpdate(prevProps) {
    const { videoId } = this.props.match.params;

    if (prevProps.match.params.videoId !== videoId) {
      axios.get(`${API_URL}/${videoId}${API_KEY}`).then((response) => {
        this.setState({
          videoData: response.data,
        });
        console.log(re)
      });
    }
  }

changeVideo = (videoId) => { 

    this.setState({
      videoData: this.state.videos.find(video => video.id === videoId),
      })
   }

  render() {
    console.log(this.state.videoData)
    console.log(this.state.videos)
    if(!this.state.videos){
            return <h2>Loading...</h2>;
        }

    return (
      <>
      <Video video={this.state.videos}/>
      <div className="container">
        <div className="container--left">
          <Info video={this.state.videoData}/>
          <Comments details={this.state.videoData}/>
        </div>
        <div className="container--right">
          <Sidebar
            videos={this.state.videos.filter(video => this.state.videoData.id !== video.id)}
            handleClick={this.changeVideo} />
        </div>
      </div>
      </>
    );
  }
}

export default HomePage;
