import '../../App.scss';
import React, { Component } from 'react';
import Video from '../../components/Video/Video.jsx';
import Info from '../../components/Info/Info.jsx';
import Comments from '../../components/Comments/Comments.jsx';
import Sidebar from '../../components/Sidebar/Sidebar.jsx';
import { API_KEY, API_URL } from '../../utils';
import axios from 'axios';



class HomePage extends Component  {



changeVideo = (id) => {
     let newVideo = this.state.selectedVideo.find(video => video.id === id)

    this.setState({
      selectedVideo: newVideo,
      })
   }

  render() {
    console.log(this.state.selectedVideo)
    return (
      <>
      <Video video={this.state.videos}/>
      <div className="container">
        <div className="container--left">
          <Info video={this.state.selectedVideo}/>
          <Comments details={this.state.selectedVideo}/>
        </div>
        <div className="container--right">
          <Sidebar
            videos={this.state.videos.filter(video => this.state.selectedVideo.id !== video.id)}
            handleClick={this.changeVideo} />
        </div>
      </div>
      </>
    );
  }
}

export default HomePage;
