import '../../App.scss';
import React, { Component } from 'react';
import Video from '../../components/Video/Video.jsx';
import Info from '../../components/Info/Info.jsx';
import Comments from '../../components/Comments/Comments.jsx';
import Sidebar from '../../components/Sidebar/Sidebar.jsx';
import { API_KEY, API_URL } from '../../utils';
import axios from 'axios';

class HomePage extends Component  {

  state = {
    videoList: [],
    selectedVideo: null
  }

  fetchVideoDetails = (videoId) => {
    axios
      .get(`${API_URL}/${videoId}${API_KEY}`)
      .then(videoDetails => {
        console.log('Individual Video Details: ', videoDetails.data);

        this.setState({
          selectedVideo: videoDetails.data
        });
      })
  }

  componentDidMount() {
    const currentVideoId = this.props.match.params.videoId;

    console.log('Current Video ID: ', currentVideoId)

    axios
      .get(`${API_URL}${API_KEY}`) 
      .then(videoResults => {
        console.log('Video Results Data: ', videoResults.data);

        this.setState({
          videoList: videoResults.data
        });
        const firstVideo = videoResults.data[0];
        const videoToLoadId = currentVideoId ? currentVideoId : firstVideo.id;
        this.fetchVideoDetails(videoToLoadId);
        console.log(vid)
      });
  }

  componentDidUpdate(prevProps) {
    const currentVideoId = this.props.match.params.videoId;
    const prevVideoId = prevProps.match.params.videoId;

    console.log('Previous: ', prevVideoId);
    console.log('Current: ', currentVideoId);

    // We need to compare the previous value of videoID URL parameter with the current value
    // and only make an API request if we are changing URLs. This is required to prevent infinite loop of render > componentDidUpdate > render
    if (currentVideoId !== prevVideoId) {
      this.fetchVideoDetails(currentVideoId);
    } 
  }
  render() {
    if(!this.state.selectedVideo){
            return <h2>Loading...</h2>;
    }

    const filterCurrentVideo = this.state.selectedVideo
      ? this.state.videoList.filter(video => video.id !== this.state.selectedVideo.id)
      : this.state.videoList;

    return ( 
      <>
      <Video video={this.state.selectedVideo}/>
      <div className="container">
        <div className="container--left">
          <Info video={this.state.selectedVideo}/>
          <Comments video={this.state.selectedVideo}/>
        </div>
        <div className="container--right">
          <Sidebar videoList={filterCurrentVideo} />
        </div>
      </div>
      </>
    );
  }
}

export default HomePage;
