import React, { Component } from 'react';
import './Sidebar.scss';
import axios from 'axios';
import { API_URL, API_KEY } from '../../utils';

export default class Sidebar extends Component {
    state = {
        videos: [],
    }

    componentDidMount() {
    axios.get(`${API_URL}videos${API_KEY}`).then((response) => {
      console.log(response);
      this.setState({
        videos: response.data.video,
      });
    });
  }
  render() {
    return (
        <section className="sidebar">
            <h6 className="sidebar__header">Next Video</h6>

                {this.state.videos.map(video => {
                    return (
                    <div
                        key={video.id}
                        className="sidebar__container"
                        onClick={() => this.state.handleClick(video.id)}
                    >
                        <div className="sidebar__img"><img
                            src={video.image}
                            alt="thumbnail for video"
                            className="sidebar__thumbnail"
                        /></div>
                        <div className="sidebar__info">
                            <h5 className="sidebar__title">{video.title}</h5>
                            <p className="sidebar__creator">{video.channel}</p>
                        </div>
                    </div>
                    )
                })}

        </section>
    )
}}
