const videoURL = videos => `${API_URL}videos/${videos}${API_KEY}`

export default class Video extends Component {
    state = {
        videos: videos[0],
    }

  componentDidMount() {
   this.getData()
  }

  getData = () => axios.get(videoURL(this.state.videos)).then(res => {
    this.setState({
      videos: res.data.videos
    })
  })


    render() {
        return (
            <div className="video">
