import '../../App.scss';
import React, { Component } from 'react';
import Video from '../../components/Video/Video.jsx';
import Info from '../../components/Info/Info.jsx';
import Comments from '../../components/Comments/Comments.jsx';
import Sidebar from '../../components/Sidebar/Sidebar.jsx';
import { API_KEY, API_URL } from '../../utils';
import axios from 'axios';



class HomePage extends Component  {

  // Set Default State Values
  state = {
    videos: [],
    selectedVideo: null,
  }

  //Grabbing video details from the API
  getVideoDetails = (videoId) => {
    axios
      .get(`${API_URL}/${videoId}${API_KEY}`)
      .then((videoDetails) => {
        this.setState({
          selectedVideo: videoDetails.data,
        });
        console.log(this.state.selectedVideo)
      })
  }

  componentDidUpdate(prevProps, _prevState) {
    console.log("PrevProps: ", prevProps.match.params.videoId)
    console.log("CurrentProps: ", this.props.match.params.videoId)
    if (prevProps.match.params.videoId === this.props.match.params.videoId) {
          return;
        } else {
          this.getVideoDetails(this.props.match.params.videoId);
          console.log('Match Video ID:', this.props.match.params.videoId);
        }
  }

  componentDidMount() {

    axios
      .get(`${API_URL}${API_KEY}`)
      .then((response) => {
        console.log('Videos:', response.data);

        this.setState({
          videos: response.data
        });

        const videoId = this.props.match.params.videoId || response.data[0].id;

        this.getVideoDetails(videoId)
      });
  }




  render() {
    console.log('Selected:', this.state.selectedVideo)

    if(!this.state.selectedVideo){
            return <h2>Loading...</h2>;
    }

    const filterCurrentVideo = this.state.selectedVideo
      ? this.state.videos.filter(video => video.id !== this.state.selectedVideo.id)
      : this.state.videos;

    return ( 
      <>
      <Video video={this.state.selectedVideo}/>
      <div className="container">
        <div className="container--left">
          <Info video={this.state.selectedVideo}/>
          <Comments video={this.state.selectedVideo}/>
        </div>
        <div className="container--right">
          <Sidebar video={filterCurrentVideo} />
        </div>
      </div>
      </>
    );
  }
}

export default HomePage;
