import '../../App.scss';
import React, { Component } from 'react';
import Header from '../../components/Header/Header.jsx';
import Video from '../../components/Video/Video.jsx';
import Info from '../../components/Info/Info.jsx';
import Comments from '../../components/Comments/Comments.jsx';
import Sidebar from '../../components/Sidebar/Sidebar.jsx';
import { API_KEY, API_URL } from '../../utils';
import axios from 'axios';



class HomePage extends Component  {

  state = {
      videos: [],
      selectedVideo: null,
  }

    getVideoDetails = (videoId) => {
        axios
            .get(`${API_URL}/${videoId}${API_KEY}`)
            .then((response) => {
                this.setState({
                    selectedVideo: response.data,
                });
                console.log(response.data)
            });
    };

    componentDidUpdate(prevProps) {
        // if the previous props are the same as this.props after setState updates this component, we don't want to setState again, because this will be an infinite loop
        if (
            prevProps.match.params.videoId !== this.props.match.params.videoId
        ) {
            this.getVideoDetail(this.props.match.params.videoId);
        }
    }

    componentDidMount() {
        axios
            .get(`${API_URL}${API_KEY}`)
            .then((response) => {
                this.setState({
                    videos: response.data,
                });

                const videoId =
                    this.props.match.params.videoId || response.data[0].id;

                this.getVideoDetail(videoId);
            });
    }

// changeVideo = (id) => {
//      let newVideo = this.state.videos.find(video => video.id === id)

//     this.setState({
//       selectedVideo: newVideo,
//      })
//    }

  render() {
    return (
      <>
      <Header />
      <Video video={this.state.videos}/>
      <div className="container">
        <div className="container--left">
          <Info video={this.state.selectedVideo}/>
          <Comments video={this.state.selectedVideo}/>
        </div>
        <div className="container--right">
          <Sidebar
            videos={this.state.videos.filter(video => this.state.selectedVideo.id !== video.id)}
            handleClick={this.changeVideo} />
        </div>
      </div>
      </>
    );
  }
}

export default HomePage;
