import Info from '../../components/Info/Info.jsx';
import Comments from '../../components/Comments/Comments.jsx';
import Sidebar from '../../components/Sidebar/Sidebar.jsx';
import { API_KEY, API_URL } from '../../utils';
import axios from 'axios';



class HomePage extends Component  {

  // Set Default State Values
  state = {
    videos: [],
    selectedVideo: null,
  }

  getVideoDetails = (videoId) => {
    axios
      .get(`${API_URL}/${videoId}${API_KEY}`)
      .then((res) => {
        this.setState({
          videoData: res.data,
        });
        console.log('Video Details:', res.data)
      })
  }

  componentDidMount() {
    const currentVideoId = this.props.match.params.videoId;
    console.log('Current ID:', currentVideoId)

    axios
      .get(`${API_URL}${API_KEY}`)
      .then((response) => {
        console.log('Videos:', response.data);

        this.setState({
          videos: response.data,
        });

        const defaultVideo = response.data[0]
        console.log('Default:', defaultVideo)

        const loadId = currentVideoId ? currentVideoId : defaultVideo.id

        this.getVideoDetails(loadId)
      });
  }

  componentDidUpdate(prevProps) {
    const currentVideoId = this.props.match.params.videoId;
    const prevVideoId = prevProps.match.params.movieId;

    console.log('Previous: ', prevVideoId);
    console.log('Current: ', currentVideoId);

    if (currentVideoId !== prevVideoId) {
      this.getVideoDetails(currentVideoId);
    } 
